import React, { useState, } from "react";

function FormContainer({ isLogin }) {
  const [formData, setFormData] = useState({ name: "", age: "", email: "", phone: "" });

  const handleChange = (event) => {
    setFormData({ ...formData, [event.target.name]: event.target.value });
  };

  const handleSubmit = (event) => {
    // Implement your login/signup logic here
    event.preventDefault();
    console.log("Form data:", formData);
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen py-12 px-4 sm:px-6 lg:px-8">
      <div className="bg-gray-800 rounded-lg shadow-md w-full max-w-md p-8 space-y-8">
        <h2 className="text-2xl font-semibold text-center text-gray-300">
          {isLogin ? "Login" : "Sign Up"}
        </h2>
        <div className="grid grid-cols-2 gap-4">
          <input
            type="text"
            name="name"
            placeholder="Name"
            className="rounded-md px-3 py-2 text-gray-700 bg-gray-200 border border-gray-300 focus:text-gray-900 focus:ring ring-indigo-500 focus:border-indigo-500"
            required
            onChange={handleChange}
          />
          <input
            type="number"
            name="age"
            placeholder="Age"
            className="rounded-md px-3 py-2 text-gray-700 bg-gray-200 border border-gray-300 focus:text-gray-900 focus:ring ring-indigo-500 focus:border-indigo-500"
            required
            onChange={handleChange}
          />
          <input
            type="email"
            name="email"
            placeholder="Email"
            className="rounded-md px-3 py-2 text-gray-700 bg-gray-200 border border-gray-300 focus:text-gray-900 focus:ring ring-indigo-500 focus:border-indigo-500"
            required
            onChange={handleChange}
          />
          <input
            type="tel"
            name="phone"
            placeholder="Phone Number"
            className="rounded-md px-3 py-2 text-gray-700 bg-gray-200 border border-gray-300 focus:text-gray-900 focus:ring ring-indigo-500 focus:border-indigo-500"
            onChange={handleChange}
          />
        </div>
        {isLogin ? (
          <button
            type="submit"
            className="inline-flex items-center px-4 py-2 rounded-md text-base font-semibold text-gray-900 bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
          >
            Login
          </button>
        ) : (
          <button
            type="submit"
            className="inline-flex items-center px-4 py-2 rounded-md text-base font-semibold text-gray-900 bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
          >
            Sign Up
          </button>
        )}
        {!isLogin && (
          <span className="text-center text-sm text-gray-400">
            Already have an account?{" "}
            <a href="#" className="text-indigo-600 hover:underline">
              Login
            </a>
          </span>
        )}
      </div>
    </div>
  );
}

export default FormContainer
