import React, { useState } from 'react';
import { useForm, Controller } from 'react-hook-form';
// import { yupResolver } from 'yup';
import * as yup from 'yup';
import axios from 'axios';

const schema = yup.object().shape({
  age: yup.number().required('Age is required'),
  email: yup.string().email('Invalid email').required('Email is required'),
  name: yup.string().required('Name is required'),
  phone: yup.string(),
  batch: yup.string().required('Batch is required'),
});

const EnrollmentForm = () => {
  const { control, handleSubmit, errors } = useForm({
    // resolver: yupResolver(schema),
  });

  const [showPopup, setShowPopup] = useState(false);
  const [paymentSuccess, setPaymentSuccess] = useState(false);

  const batches = ['6-7AM', '7-8AM', '8-9AM', '5-6PM'];

  const onSubmit = async (data) => {
    // You need to replace 'YOUR_PAYMENT_API_URL' with the actual URL for payment processing
    try {
      // Assuming a successful payment if the request goes through
      await axios.post('YOUR_PAYMENT_API_URL', {
        ...data,
        // Include other payment-related data if needed
      });

      setPaymentSuccess(true);
      setShowPopup(true);
    } catch (error) {
      console.error('Payment failed:', error);
      setPaymentSuccess(false);
      setShowPopup(true);
    }
  };

  const handleClosePopup = () => {
    setShowPopup(false);
    // Add any additional logic to navigate back to the main screen if needed
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-900 text-white">
      <div className="bg-gray-800 rounded-lg p-8 max-w-md w-full">
        <h2 className="text-2xl font-bold mb-6">Enrollment Form</h2>
        <form onSubmit={handleSubmit(onSubmit)}>
          <div className="mb-4">
            <label className="block text-sm font-bold mb-2" htmlFor="age">
              Age*
            </label>
            <Controller
              as={<input />}
              control={control}
              name="age"
              placeholder="Enter Age"
              type="number"
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            />
            <p className="text-red-500 text-xs italic">{errors.age?.message}</p>
          </div>
          <div className="mb-4">
            <label className="block text-sm font-bold mb-2" htmlFor="email">
              Email*
            </label>
            <Controller
              as={<input />}
              control={control}
              name="email"
              placeholder="Enter Email"
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            />
            <p className="text-red-500 text-xs italic">{errors.email?.message}</p>
          </div>
          <div className="mb-4">
            <label className="block text-sm font-bold mb-2" htmlFor="name">
              Name*
            </label>
            <Controller
              as={<input />}
              control={control}
              name="name"
              placeholder="Enter Name"
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            />
            <p className="text-red-500 text-xs italic">{errors.name?.message}</p>
          </div>
          <div className="mb-4">
            <label className="block text-sm font-bold mb-2" htmlFor="phone">
              Phone Number (Optional)
            </label>
            <Controller
              as={<input />}
              control={control}
              name="phone"
              placeholder="Enter Phone Number"
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            />
          </div>
          <div className="mb-4">
            <label className="block text-sm font-bold mb-2" htmlFor="batch">
              Select Batch*
            </label>
            <Controller
              as={
                <select
                  className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                >
                  {batches.map((batch) => (
                    <option key={batch} value={batch}>
                      {batch}
                    </option>
                  ))}
                </select>
              }
              control={control}
              name="batch"
              defaultValue=""
            />
            <p className="text-red-500 text-xs italic">{errors.batch?.message}</p>
          </div>
          <button
            type="submit"
            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
          >
            Pay
          </button>
        </form>

        {/* Popup for Payment Result */}
        {showPopup && (
          <div className="fixed inset-0 bg-gray-900 bg-opacity-75 flex items-center justify-center">
            <div className="bg-white p-8 rounded-lg max-w-md">
              <h2 className="text-2xl font-bold mb-4">
                {paymentSuccess ? 'Payment Successful!' : 'Payment Failed!'}
              </h2>
              <p className="text-gray-700">
                {paymentSuccess
                  ? 'Thank you for your enrollment! Your payment was successful.'
                  : 'Sorry, it seems there was an issue with your payment. Please try again.'}
              </p>
              <button
                className="mt-4 bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                onClick={handleClosePopup}
              >
                Close
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default EnrollmentForm;
