import React, { useState } from 'react';
import axios from 'axios';

const EnrollmentForm = () => {
  const [enrollmentName, setEnrollmentName] = useState('');
  const [selectedBatch, setSelectedBatch] = useState('');
  const [showPopup, setShowPopup] = useState(false);
  const [paymentSuccess, setPaymentSuccess] = useState(false);

  const batches = ['6-7AM', '7-8AM', '8-9AM', '5-6PM'];

  const handlePay = async () => {
    // You need to replace 'YOUR_PAYMENT_API_URL' with the actual URL for payment processing
    try {
      // Assuming a successful payment if the request goes through
      await axios.post('YOUR_PAYMENT_API_URL', {
        enrollmentName,
        selectedBatch,
        // Include other payment-related data if needed
      });

      // setPaymentSuccess(true);
      // setShowPopup(true);
    } catch (error) {
      console.error('Payment failed:', error);
      setPaymentSuccess(false);
      setShowPopup(true);
    }
  };

  const handleClosePopup = () => {
    setShowPopup(false);
    // Add any additional logic to navigate back to the main screen if needed
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-900 text-white">
      <div className="bg-gray-800 rounded-lg p-8 max-w-md w-full">
        <h2 className="text-2xl font-bold mb-6">Enrollment Form</h2>
        <div className="mb-4">
          <label className="block text-sm font-bold mb-2" htmlFor="enrollmentName">
            Enrollment Name
          </label>
          <input
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            id="enrollmentName"
            type="text"
            placeholder="Enter Enrollment Name"
            value={enrollmentName}
            onChange={(e) => setEnrollmentName(e.target.value)}
          />
        </div>
        <div className="mb-4">
          <label className="block text-sm font-bold mb-2" htmlFor="batch">
            Select Batch
          </label>
          <select
            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            id="batch"
            value={selectedBatch}
            onChange={(e) => setSelectedBatch(e.target.value)}
          >
            <option value="" disabled>
              Select Batch
            </option>
            {batches.map((batch) => (
              <option key={batch} value={batch}>
                {batch}
              </option>
            ))}
          </select>
        </div>
        <button
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
          onClick={handlePay}
        >
          Pay
        </button>

        {/* Popup for Payment Result */}
        {showPopup && (
          <div className="fixed inset-0 bg-gray-900 bg-opacity-75 flex items-center justify-center">
            <div className="bg-white p-8 rounded-lg max-w-md">
              <h2 className="text-2xl font-bold mb-4">
                {paymentSuccess ? 'Payment Successful!' : 'Payment Failed!'}
              </h2>
              <p className="text-gray-700">
                {paymentSuccess
                  ? 'Thank you for your enrollment! Your payment was successful.'
                  : 'Sorry, it seems there was an issue with your payment. Please try again.'}
              </p>
              <button
                className="mt-4 bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                onClick={handleClosePopup}
              >
                Close
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default EnrollmentForm;
