// PaymentForm.js
import React, { useState } from 'react';

const PaymentForm = () => {
  const [upiId, setUpiId] = useState('');
  const [processing, setProcessing] = useState(false);

  const handlePayment = () => {
    setProcessing(true);

    // Simulate payment processing delay (replace this with your actual payment processing logic)
    setTimeout(() => {
      setProcessing(false);
    }, 3000);
  };

  return (
    <div className="bg-gray-800 rounded-lg p-8 max-w-md w-full b-">
      <h2 className="text-2xl font-bold mb-6 text-white">Payment Form</h2>
      <div className="mb-4">
        <label className="block text-sm font-bold mb-2 text-white" htmlFor="upiId">
          UPI ID*
        </label>
        <input
          type="text"
          className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline bg-gray-700"
          id="upiId"
          placeholder="Enter UPI ID"
          value={upiId}
          onChange={(e) => setUpiId(e.target.value)}
          required
        />
      </div>
      <button
        className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
        onClick={handlePayment}
      >
        Pay Now
      </button>

      {/* Processing Popup */}
      {processing && (
        <div className="fixed inset-0 bg-gray-900 bg-opacity-75 flex items-center justify-center">
          <div className="bg-white p-8 rounded-lg max-w-md">
            <h2 className="text-2xl font-bold mb-4">Processing Payment</h2>
            <p className="text-gray-700">Please wait while we process your payment...</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default PaymentForm;
